SELECT DISTINCT tblTagesplan.idTagesplan, tblTagesplan.txtTagesplanName, tblVeranstaltung.txtVeranstaltungName, tblBewohner.calcBewohnerName, tblPersonal.calcPersonalName
FROM ((tblVeranstaltung INNER JOIN (tblBewohner INNER JOIN tblBewohnerVeranstaltung ON tblBewohner.idBewohner = tblBewohnerVeranstaltung.frkBewohner) ON tblVeranstaltung.idVeranstaltung = tblBewohnerVeranstaltung.frkVeranstaltung) INNER JOIN (tblPersonal INNER JOIN tblPersonalVeranstaltung ON tblPersonal.idPersonal = tblPersonalVeranstaltung.frkPersonal) ON tblVeranstaltung.idVeranstaltung = tblPersonalVeranstaltung.frkVeranstaltung) INNER JOIN (tblTagesplan INNER JOIN tblVeranstaltungTagesplan ON tblTagesplan.idTagesplan = tblVeranstaltungTagesplan.frkTagesplan) ON tblVeranstaltung.idVeranstaltung = tblVeranstaltungTagesplan.frkVeranstaltung
WHERE (((tblTagesplan.idTagesplan)=2));
